import net from 'net';
import http from 'http';

interface HTTPRequest {
  method: string;
  url: string;
  headers: Record<string, string>;
  body?: string;
}

function parseRequest(data: Buffer): HTTPRequest {
  const requestString = data.toString();
  const [requestLine, ...headersAndBody] = requestString.split('\r\n');
  const [method, url] = requestLine.split(' ');
  const headers: Record<string, string> = {};
  let body = '';
  let bodyStartIndex = headersAndBody.indexOf('');

  if (bodyStartIndex > -1) {
    body = headersAndBody.slice(bodyStartIndex + 1).join('\r\n');
  }

  for (const line of headersAndBody.slice(0, bodyStartIndex)) {
    const [key, value] = line.split(': ');
    if (key && value) { // Check if key and value exist to avoid errors.
      headers[key.toLowerCase()] = value.trim();
    }
  }

  return { method, url, headers, body };
}

const server = http.createServer((req, res) => {
  let requestData = Buffer.from([]);

  req.on('data', (chunk: Buffer) => {
    requestData = Buffer.concat([requestData, chunk]);
  });

