```typescript
import * as Chart from 'chart.js';

class DataChart extends HTMLElement {
  private chart: Chart | null = null;
  private canvas: HTMLCanvasElement | null = null;

  constructor() {
    super();
    this.attachShadow({ mode: 'open' });
  }

  static get observedAttributes() {
    return ['data-source'];
  }

  async attributeChangedCallback(name: string, oldValue: string | null, newValue: string | null) {
    if (name === 'data-source' && newValue !== oldValue) {
      await this.updateChart(newValue);
    }
  }

  private async updateChart(dataSource: string | null) {
    if (!dataSource || !this.shadowRoot) return;

    const data = await fetch(dataSource).then(res => res.json());

    if (!this.canvas) {
      const style = document.createElement('style');
      style.textContent = `:host { display: block; width: 400px; height: 300px; }`;
      this.shadowRoot.appendChild(style);
      this.canvas = document.createElement('canvas');
      this.shadowRoot.appendChild(this.canvas);
    }

    if (this.chart) {
      this.chart.destroy();
    }

    this.chart = new Chart(this.canvas, {
      type: 'line',
      data,
    });
  }

  disconnectedCallback() {
    if (this.chart) {
      this.chart.destroy();
    }
  }
}

customElements.define('data-chart', DataChart);

export default DataChart;
```

