import { useState, useEffect } from 'react';

interface PanelState {
  x: number;
  y: number;
  width: number;
  height: number;
}

const usePanel = (panelId: string): PanelState => {
  const initialPanelState: PanelState = {
    x: 0,
    y: 0,
    width: 300,
    height: 200,
  };

  const [panelState, setPanelState] = useState<PanelState>(initialPanelState);

  useEffect(() => {
    const storedState = localStorage.getItem(`panel-${panelId}`);
    // Provide a default value to JSON.parse to handle null cases.
    setPanelState(JSON.parse(storedState || JSON.stringify(initialPanelState)));
  }, [panelId]);

  useEffect(() => {
    localStorage.setItem(`panel-${panelId}`, JSON.stringify(panelState));
  }, [panelState, panelId]);

  return panelState;
};

export default usePanel;

