}

const DataDisplay = () => {
  const [data, setData] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchData('/get-data')
      .then(response => setData(response))
      .catch(err => setError(err));
  }, []);

  return (
    <div>
      <div id='output'>{error ? <span>{error}</span> : data}</div>
    </div>
  );
};
```
